// client/src/components/Profile.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Profile = () => {
  const navigate = useNavigate();
  const [profileUser, setProfileUser] = useState(null);
  const [stats, setStats] = useState({ followers: 0, following: 0 });
  const [isFollowing, setIsFollowing] = useState(false);
  const [loading, setLoading] = useState(false);
  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    if (!currentUser) {
      navigate('/login');
      return;
    }

    const loadProfile = async () => {
      try {
        // For demo: show own profile
        const userRes = await axios.get(`http://localhost:5000/api/auth/me`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setProfileUser(userRes.data);

        // Get stats
        const statsRes = await axios.get(`http://localhost:5000/api/follow/${userRes.data._id}/stats`);
        setStats(statsRes.data);

        // Check follow status (if viewing another user, add logic here)
        setIsFollowing(false); // Default for self
      } catch (err) {
        console.error('Profile load error');
      }
    };

    loadProfile();
  }, [navigate, currentUser]);

  const handleFollow = async () => {
    if (!currentUser || !profileUser) return;

    setLoading(true);
    try {
      const res = await axios.post(`http://localhost:5000/api/follow/${profileUser._id}`, {
        userId: currentUser.id
      });
      
      setIsFollowing(res.data.action === 'follow');
      
      // Refresh stats
      const statsRes = await axios.get(`http://localhost:5000/api/follow/${profileUser._id}/stats`);
      setStats(statsRes.data);
    } catch (err) {
      alert('Failed to update follow status');
    } finally {
      setLoading(false);
    }
  };

  if (!profileUser) return <div>Loading...</div>;

  return (
    <div className="max-w-md mx-auto p-6">
      <div className="text-center">
        <div className="w-24 h-24 rounded-full bg-nexora-primary flex items-center justify-center text-white text-2xl font-bold mx-auto mb-4">
          {profileUser.name[0]}
        </div>
        <h1 className="text-2xl font-bold text-gray-900">{profileUser.name}</h1>
        <p className="text-gray-600 mt-1">{profileUser.email}</p>
        <p className="text-gray-700 mt-4">{profileUser.bio || "No bio yet."}</p>
        
        <div className="mt-8 grid grid-cols-3 gap-4 text-center">
          <div>
            <div className="text-xl font-bold text-nexora-primary">12</div>
            <div className="text-sm text-gray-600">Posts</div>
          </div>
          <div>
            <div className="text-xl font-bold text-nexora-primary">{stats.followers}</div>
            <div className="text-sm text-gray-600">Followers</div>
          </div>
          <div>
            <div className="text-xl font-bold text-nexora-primary">{stats.following}</div>
            <div className="text-sm text-gray-600">Following</div>
          </div>
        </div>

        {/* Follow button (hidden for self-profile in demo) */}
        {currentUser?.id !== profileUser._id && (
          <button
            onClick={handleFollow}
            disabled={loading}
            className={`mt-6 px-6 py-2 rounded-full font-medium ${
              isFollowing
                ? 'bg-gray-200 text-gray-700'
                : 'bg-nexora-primary text-white hover:bg-nexora-secondary'
            }`}
          >
            {loading ? '...' : isFollowing ? 'Following' : 'Follow'}
          </button>
        )}
      </div>
    </div>
  );
};

export default Profile;
